INSERT INTO `contents` VALUES ('about-text-and-grid-text_en', 'Our community is full of talented individuals.&nbsp;<br />\n<br />\nIt is composed of big brands, businesses, startups, and professionals linked to the retail industry.&nbsp;<br />\n<br />\nJoin a powerful network of partners to co-create the products and solutions of the future.<br />\n<br />\nThe retail revolution has already started.&nbsp;', '0000-00-00 00:00:00');
INSERT INTO `contents` VALUES ('about-text-and-grid-text_es', 'Nuestra comunidad es puro talento.<br />\n<br />\nEst&aacute; formada por grandes marcas, empresas, startups y profesionales del sector del retail.<br />\n<br />\n&Uacute;nete a una potente red para co-crear las soluciones expositivas del futuro.<br />\n<br />\nLa revoluci&oacute;n del retail ya se ha puesto en marcha.', '0000-00-00 00:00:00');
