INSERT INTO `contents` VALUES ('community-intro2-btb--description_es', 'Crea una experiencia integral para tus clientes aprovechando todas las posibilidades creativas que te ofrece nuestro showroom. Un espacio flexible y polivalente donde exponer tus soluciones, testar nuevas l&iacute;neas de producto y hacer demos con capacidad de seducir. Renueva tu capacidad de sorprender co-creando con otros miembros para montar exposiciones colaborativas. &iquest;Necesitas montar un evento privado? &iexcl;Cuenta con nosotros!<br />\nPuedes alquilar nuestro showroom en Madrid siempre que quieras.', '0000-00-00 00:00:00');
