INSERT INTO `services` VALUES (9, 'Construcció de marca', 'Dissenyem la teva imatge de marca i establim mecanismes de posicionament dins del sector del vi per fer arribar la teva empresa al seu target adequat.', 'ca', 1, 32, 1, 'EMPELTAR');
INSERT INTO `services` VALUES (10, 'Esdeveniments', 'Dissenyem i produïm esdeveniments en funció dels teus objectius. La versatilitat és una actitud, l’experiència una garantia i l’originalitat un segell. Busquem ressò a través de la innovació.', 'ca', 1, 26, 5, 'CUPAJAR');
INSERT INTO `services` VALUES (11, 'Assessorament de negoci', 'Explica’ns el teu cas. Comences el teu projecte personal? Les xarxes socials et fan pànic? Vols donar una nova imatge a la teva marca però no saps on acudir? Des de kool fem consultoria en comunicació però també et donem les eines més idònies per ajudar-te a fer el primer pas en el teu negoci vinícola.', 'ca', 1, 24, 6, 'SOBRETAULA');
INSERT INTO `services` VALUES (12, 'Relacions públiques 2.0', 'Gràcies a la nostra especialització en el mercat vinícola, comptem amb una àmplia xarxa de contactes que et posem a l’abast. Treballem també per enfortir els teus vincles amb mitjans de premsa especialitzats i influencers.', 'ca', 1, 33, 4, 'BRINDAR');
INSERT INTO `services` VALUES (13, 'Creació de contingut', 'Narrem històries per ser consumides. Cada producte, empresa o servei té la seva pròpia, nosaltres t’ajudem a donar-li forma i explicar-la través d\'imatges i paraules que defineixin la teva singularitat i diferenciació. Emocionem al winelover amb conceptes trencadors.', 'ca', 1, 27, 3, 'FERMENTAR');
INSERT INTO `services` VALUES (14, 'Estratègia digital / Pàgines web', 'Creem, Gestionem i planifiquem campanyes digitals adaptades a cada plataforma de comunicació. Analitzem el teu cas en clau de vi i dinamitzem la teva marca a través de la gestió de les xarxes socials incrementant la visibilitat, l’audiència i l’engagement de cadascuna de les accions realitzades. També fem pàgines web a mida amb molta cura en el disseny, la seva navegació i usabilitat.', 'ca', 1, 31, 2, 'VEREMAR');
INSERT INTO `team` VALUES (4, 'Hola!', '', 'Comuniquem amb passió i creativitat.\nProduïm esdeveniments inoblidables.\nFem estratègies de branding i màrqueting a mida.\nSempre des de l’especialització del món del vi i el seu entorn més proper.\nEl consum del vi pot créixer i la nostra fita és fer-ho a través de la comunicació.', 'ca', 1, 63, 1);
INSERT INTO `team` VALUES (5, 'L\'art de beure en bona companyia!', '', 'De petits ens van ensenyar a lluitar pels nostres somnis, i ens declarem vencedors. \nSom un equip de professionals formats en el camp de la comunicació i la sumilleria i en constant (r)evolució.\nInterpretem el teu missatge amb positivisme, i el transformem en idees tangibles.\nEls límits se\'ls posa un mateix, i nosaltres et donem les eines per trencar-los. \n\nI recorda que amb nosaltres, \nYou\'ll never wine alone. ', 'ca', 1, 50, 2);
