INSERT INTO `ticket` VALUES (147, 1, 24, 4, 5, 3, 7, 1, 1, 78, 1, 0, '2017-03-13 14:57:37', '2017-03-19 00:17:44', 'Fotos portfolio', 'Necesitamos otro sistema para colgar las fotos en los proyectos, algo mucho más sencillo, tenemos dos opciones: copiar el sistema de blog de Milokka o el de Moskada en el que pueden elegir plantilla. Cual crees que puede ir mejor en su caso?', '', '', 230);
