INSERT INTO `ticket` VALUES (171, 1, 22, 1, 4, 5, 8, 1, 1, 51, 1, 0, '2017-04-26 10:19:22', '2017-11-29 17:54:35', 'La palabra fuera de stock no destaca cuando abrimos el producto, por favor la subimos y hacemos que destaque.', '1. Por un lado subimos el fuera de stock a la altura de referencia y lo ponemo sen rojo, código 	#DC143C\n\n2. Por otro lado mantenemos la etiqueta de Sold out de la miniatura en el primer visual del bolso abierto.', '', '', 254);
